mapper.insert:
insert
into member set ID=#{ID},PW=#{PW},
nickname=#{nickname},email=#{email}, phoneNo=#{phoneNo}

mapper.select:
select * from member


		where uid = #{item}


		where ID = #{item}


		where nickname = #{item}


		where authKey = #{item}

mapper.update:
update member set updateDate = now()

	, PW = #{PW}


	, nickname = #{nickname}


	, email = #{email}


	, phoneNo = #{phoneNo}

where uid = #{uid}
